class Solution:
    def possibleStringCount(self, word: str, k: int) -> int:
        MOD = 10 ** 9 + 7
        n = len(word)
        segment = []
        idx = 0
        while idx < n:
            next_segment_start = idx + 1
            while next_segment_start < n and word[idx] == word[next_segment_start]:
                next_segment_start += 1
            segment.append(next_segment_start - idx)
            idx = next_segment_start
        previous_prefix_sum = [0] * (k + 1) + [1]
        for segment_idx in range(len(segment) - 1, -1, -1):
            curr_prefix_sum = [0] * (k + 2)
            for count in range(k + 1):
                segment_count = segment[segment_idx]
                ans = 0
                left = count + 1
                right = min(k - 1, count + segment_count)
                other = max(0, count + segment_count - k + 1)
                ans = ((ans + previous_prefix_sum[right + 1]) % MOD - previous_prefix_sum[left] + MOD) % MOD
                ans = (ans + ((previous_prefix_sum[-1] - previous_prefix_sum[-2] + MOD) % MOD * other) % MOD) % MOD
                curr_prefix_sum[count + 1] = (curr_prefix_sum[count] + ans) % MOD
            previous_prefix_sum = curr_prefix_sum
        return previous_prefix_sum[1]


class Solution2:
    def possibleStringCount(self, word: str, k: int) -> int:
        """
       Further optimized version using rolling array and early termination
       """

        MOD = 10 ** 9 + 7

        if not word:
            return 1 if k == 0 else 0

        # Group consecutive identical characters
        groups = []
        i = 0
        while i < len(word):
            count = 1
            while i + 1 < len(word) and word[i + 1] == word[i]:
                count += 1
                i += 1
            groups.append(count)
            i += 1

        n = len(groups)

        # Further optimization: we only need to track up to k + reasonable buffer
        # Since we only care about lengths >= k
        max_len = min(sum(groups), k + 100)  # Small buffer for safety

        # Use rolling array to save space
        prev_dp = [0] * (max_len + 1)
        curr_dp = [0] * (max_len + 1)

        prev_dp[0] = 1

        for i in range(n):
            group_size = groups[i]

            # Reset current DP array
            for j in range(max_len + 1):
                curr_dp[j] = 0

            # Create prefix sum array
            prefix = [0] * (max_len + 2)
            for j in range(max_len + 1):
                prefix[j + 1] = (prefix[j] + prev_dp[j]) % MOD

            # Fill current DP array
            for j in range(1, max_len + 1):
                left = max(0, j - group_size)
                right = j
                curr_dp[j] = (prefix[right] - prefix[left] + MOD) % MOD

            # Swap arrays
            prev_dp, curr_dp = curr_dp, prev_dp

        # Sum all ways with original length >= k
        result = 0
        for length in range(k, max_len + 1):
            result = (result + prev_dp[length]) % MOD

        return result


